import React from 'react';
import './AboutMeStyles.scss'

import DATA from '../../assets/data/AboutMeData.js'

import {Card, CardHeader, CardMedia, CardContent, CardActions, makeStyles,CardActionArea, Typography} from '@material-ui/core'

const useStyles = makeStyles((theme)=>({
    profileContainer: {
        width:'30%',
    }
}))

class AboutMe extends React.Component{
    constructor(){
        super()

        this.state={
            profile_data:DATA
        }
    }

    render(){
        const {profile_data} = this.state
        const {name,imageUrl,desc} = {...profile_data}
        const classes = useStyles();

        return(
            <Card className={classes.profileContainer}>
                <CardActionArea>
                    <CardMedia
                        Component='img'
                        alt='Harry Russin'
                        height='60%'
                        image={imageUrl}
                        title='profile photo'
                    />
                    <CardContent>
                        <Typography gutterBottom varient='h5' Component='h2'>{name}</Typography>
                    </CardContent>

                </CardActionArea>
            </Card>
        )
        
    }
}

export default AboutMe;
