import React from 'react';
import './AboutMeStyles.scss'

import DATA from '../../assets/data/AboutMeData.js'

class AboutMe extends React.Component{
    constructor(){
        super()

        this.state={
            profile_data:DATA
        }
    }

    render(){
        const {profile_data} = this.state
        const {name,imageUrl,desc} = {...profile_data}
        return(
            <div className='profile-container'>
                <img src={imageUrl} className='profile-photo'></img>
                <div className="profile-name">{name}</div>
                <div className="profile-desc">{desc}</div>
            </div>
        )
    }
}

export default AboutMe;
