import React,{useState} from 'react'
import {Container,Header,Icon,Button} from 'semantic-ui-react'
import { global } from 'yargs'
import './App.css'

const Headers = ()=>{

    const [title,setTitle] = useState('HarryRussin.edu')
    
    const date = ()=>{
        var time = Date();
        global(time)
        setTitle(time)
    }

    const toggle = ()=>{
        if (title==='HarryRussin.edu'){
            var ids;
            ids = setInterval(()=>{date()},1000);
        }
        if (title===time) {
            clearInterval(ids)
            setTitle('HarryRussin.edu')
        }
        
    }

    return(
    <main>
        <Container className='margins'>
        <Header block>
            <p>{title}</p>
        </Header>
        </Container>
        <Button onClick={toggle}>toggle</Button>
    </main>
    )
}

export default Headers
