import React,{useState} from 'react'
import {Container,Header,Icon,Button} from 'semantic-ui-react'
import './App.css'

const Headers = ()=>{

    const [title,setTitle] = useState('HarryRussin.edu')
    

    const toggle = ()=>{
        if (title==='HarryRussin.edu'){
            
            const current = new Date();
            let day = Date.getDay()
            let month = Date.getMonth()
            let time = toString(day)+' '+toString(month)
            setTitle(time)
        }
        else{
            setTitle('HarryRussin.edu')
        }
        
    }

    return(
    <main>
        <Container className='margins'>
        <Header block>
            <p>{title}</p>
        </Header>
        </Container>
        <Button onClick={()=>toggle()}>toggle</Button>
    </main>
    )
}

export default Headers
