import logo from './logo.svg';
import './App.css';
import {Button,ButtonContent,ButtonGroup,Icon, Popup} from 'semantic-ui-react'
import React, {useState} from 'react'

function App() {

  const [clicks,setClicks] = useState(0);

  const handleClick = ()=>{
    setClicks(clicks+1);
  }

  return (
      <div className="button">
      <Popup>
      <ButtonGroup>
        <Button animated='fade' onClick={()=>handleClick()}>
          <ButtonContent hidden>Shop</ButtonContent>
          <ButtonContent visible><Icon name='shop'/></ButtonContent>
        </Button>
        <Button.Or/>
        <Button animated='fade' onClick={()=>handleClick()}>
          <ButtonContent visible><Icon name='home'/></ButtonContent>
          <ButtonContent hidden>Home</ButtonContent>
        </Button>
      </ButtonGroup>
      </Popup>
      <p>{clicks} clicks</p>
      </div>

  );
}

export default App;
