import React,{useState} from 'react'
import {Container,Header,Icon,Button} from 'semantic-ui-react'
import './App.css'

const Headers = ()=>{

    const [title,setTitle] = useState('HarryRussin.edu')
    var toggled = false
    
    const date = ()=>{
        var time = Date();
        setTitle(time)
    }

    const toggle = ()=>{
        if (title==='HarryRussin.edu' && toggled === false){
            var ids = setInterval(()=>date(),1000);
            toggled = true
        }
        else{
            clearInterval(ids);
            setTitle('HarryRussin.edu');
        }
        toggled = false
    }

    return(
    <main>
        <Container className='margins'>
        <Header block>
            <p>{title}</p>
        </Header>
        </Container>
        <Button onClick={()=>toggle()}>toggle</Button>
    </main>
    )
}

export default Headers
